import { useState, useEffect } from "react";
import { Upload, Image } from "antd";
import { ReloadOutlined, EyeOutlined, DeleteOutlined } from "@ant-design/icons";
import axios from "axios";
import ImgCrop from "antd-img-crop";
import type { UploadFile, UploadProps } from "antd/es/upload/interface";

import { useRecoilState } from "recoil";
import { writeArticleContext } from "../index";

const Cover = () => {
  const [fileList, setFileList] = useState<UploadFile[]>([]);
  const [previewIsVisible, setPreviewIsVisible] = useState(false);
  const [articleData, setArticleData] = useRecoilState(writeArticleContext);

  useEffect(() => {
    let { cover_file_name, cover_url } = articleData;
    if (cover_file_name && cover_url) {
      setFileList([
        {
          uid: cover_file_name,
          name: cover_file_name,
          status: "done",
          url: cover_url,
        },
      ]);
    }
  }, [articleData]);

  const onChange: UploadProps["onChange"] = ({ fileList: newFileList }) => {
    if (newFileList.length && newFileList[0]?.status == "done") {
      setFileList([
        {
          uid: newFileList[0].uid,
          name: newFileList[0]?.response?.data?.file_name,
          status: "done",
          url: newFileList[0]?.response?.data?.file_href,
        },
      ]);
      setArticleData(_data => ({
        ..._data,
        cover_file_name: newFileList[0]?.response?.data?.file_name,
        cover_url: newFileList[0]?.response?.data?.file_href,
      }));
    } else {
      setFileList(newFileList);
    }
  };
  return (
    <>
      <ImgCrop aspect={3 / 2} rotate quality={1}>
        <Upload
          action={`${axios.defaults.baseURL}/static/cover`}
          listType="picture"
          showUploadList={false}
          fileList={fileList}
          onChange={onChange}
          name="image"
          accept="image/*"
          headers={{
            authorization: localStorage.token,
          }}
          maxCount={1}
          className="cursor-pointer"
        >
          <div className="w-[160px] h-[120px] border border-dashed border-slate-400 hover:border-blue-500 rounded flex items-center justify-center overflow-hidden">
            {fileList.length < 1 ? (
              "+ Upload"
            ) : (
              <Image
                className="w-[160px] h-[120px]"
                src={fileList[0].url}
                fallback="data:image/png;base64,"
                placeholder={<ReloadOutlined spin />}
                alt="预览图片"
                preview={{
                  visible: previewIsVisible,
                  onVisibleChange: value => setPreviewIsVisible(value),
                  mask: (
                    <div onClick={e => e.stopPropagation()}>
                      <span onClick={() => setPreviewIsVisible(true)}>
                        <EyeOutlined />
                      </span>
                      <span
                        className="ml-4"
                        onClick={() => {
                          setPreviewIsVisible(false);
                          setArticleData(_data => ({
                            ..._data,
                            cover_file_name: null,
                            cover_url: null,
                          }));
                          setFileList([]);
                        }}
                      >
                        <DeleteOutlined />
                      </span>
                    </div>
                  ),
                }}
              />
            )}
          </div>
        </Upload>
      </ImgCrop>
    </>
  );
};
export default Cover;
